import React, { useEffect } from "react";
import ReactDOM from 'react-dom';
import { Grid, Container } from "@material-ui/core";
import "./style/Main.css";
import img1 from "./img/img4.png";
import img2 from "./img/img5.png";
import img3 from "./img/img1.png"
import img6 from "./img/img6.png";
import img7 from "./img/img7.png";
import img8 from "./img/img8.png";

const Main = () => { 

	let imgArr = new Array();

	imgArr[0] = img1
	imgArr[1] = img2


	function showImage() { 

		var imgNum = Math.round(Math.random()*3); 

		var objImg = document.getElementById("introimg"); 

		objImg.src = imgArr[imgNum]; 

		setTimeout(showImage, 1000);

	}

		return (
			<Grid xs={12} className="main">
			  <Grid>
			    <img class = "mainImg" src={img1} style={{width:"100%"}}/>
			  </Grid>

			  <Container>
				  <Grid className="mainContent">
						<p>
							세탁품질
						</p>
						<p>
							타협없는
						</p>
						<p>
							고퀄리티
						</p>
					</Grid>
					<Grid className="wrapper">
						<p className = "laudaryContent" style={{textAlign:"left"}}> 
						수백만 점을 세탁해도 고객은 단 한 점의 옷으로 평가하기에, 세탁만세는 허상을 판매하지 않습니다. 출혈경쟁의 원가부담을 값싼 노동력과 저급한 원료로 대체하지 않습니다. 품질과 직결되는 수고로운 일에 기교를 부리지 않습니다. 오직 고객과 세탁에 집중합니다.
						</p>
					</Grid>

					<Grid>
				  	<div class="galleryThumbnailContent load" style="background-image: url(&quot;https://contents.sixshop.com/thumbnails/uploadedFiles/92052/default/image_1589435302250_1000.jpg&quot;);" data-ratio="0.6666666666666666"></div>
					</Grid>


			  </Container>


			</Grid>
		)
	}

export default Main;

